/*
 * Worker process module
 *
 * Description:  This module will be included and executed when a worker starts
 *-----------------------------------------------------
 * Author: Lars van der Schans
 * Email:  lars@wodanbrothers.com
 *-----------------------------------------------------
 */

var config = {},
    log = {},
    http, https, parseRequest, debugInfo, clientInfo, cookies, headers;

module.exports = function(server, cluster) {
    config = server.config,
    log = server.log;

    return {
        execute : function () {
            // Threaded code, all this code will be executed for each thread
            server.cluster.isMaster = true;
            server.cluster.isWorker = false;

            var write = function (request, response) {
                headers.addResponseHeader(request, response, {'Content-Type': 'text/html'});
                headers.addResponseHeader(request, response, {'Set-Cookie': 'mycookie=test'});
                response.headers
                response.writeHead(200, response.headers);
                response.write(response.body);
                response.end();
            }

            var forceHTTPS = function (request, response) {
                response.writeHead(302, {'Location': 'https://' + request.headers.host + request.url});
                response.end();
            }

            var respond = function (request, response) {
                response.headers = {};
                response.body = '';
                // Clone array because we don't want to rommel with the master object
                var seqArr = sequences[request.method.toUpperCase()].slice(0);
                server.helpers.syncIt(
                    request,
                    response,
                    seqArr
                );
            }

            parseRequest = require('@dyflexis/parse-request')(server);
            debugInfo = require('@dyflexis/server-info')(server);
            clientInfo = require('@dyflexis/client-info')(server);
            headers = require('@dyflexis/headers')(server);
            cookies = require('@dyflexis/cookies')(server);
            http = require('@dyflexis/http-server')(server);

            if (config.global.useSPDY == true) {
                https = require('@dyflexis/spdy-server')(server);
            } else {
                https = require('@dyflexis/https-server')(server);
            }
            var sequences = {};
            sequences.POST = [
                cookies.getCookies,
                clientInfo.getInfo,
                debugInfo.addForm,
                parseRequest.getPost,
                debugInfo.requestInfo,
                debugInfo.clientInfo,
                debugInfo.addConfig,
                write
            ];
            sequences.GET = [
                cookies.getCookies,
                clientInfo.getInfo,
                debugInfo.addForm,
                parseRequest.getGet,
                debugInfo.requestInfo,
                debugInfo.clientInfo,
                debugInfo.addConfig,
                write
            ];
            sequences.PUT = [
                clientInfo.getInfo,
                debugInfo.addForm,
                parseRequest.getPost,
                parseRequest.getGet,
                debugInfo.requestInfo,
                debugInfo.clientInfo,
                write
            ];
            sequences.DELETE = [
                cookies.getCookies,
                clientInfo.getInfo,
                debugInfo.addForm,
                parseRequest.getPost,
                parseRequest.getGet,
                debugInfo.requestInfo,
                debugInfo.clientInfo,
                write
            ];
            sequences.OPTIONS = [
                cookies.getCookies,
                clientInfo.getInfo,
                debugInfo.addForm,
                parseRequest.getPost,
                parseRequest.getGet,
                debugInfo.requestInfo,
                debugInfo.clientInfo,
                write
            ];
            sequences.HEAD = [
                cookies.getCookies,
                clientInfo.getInfo,
                debugInfo.addForm,
                parseRequest.getPost,
                parseRequest.getGet,
                debugInfo.addInfo,
                write
            ];
            sequences.TRACE = [
                cookies.getCookies,
                clientInfo.getInfo,
                debugInfo.addForm,
                parseRequest.getPost,
                parseRequest.getGet,
                debugInfo.requestInfo,
                debugInfo.clientInfo,
                write
            ];
            sequences.CONNECT = [
                cookies.getCookies,
                clientInfo.getInfo,
                debugInfo.addForm,
                parseRequest.getPost,
                parseRequest.getGet,
                debugInfo.requestInfo,
                debugInfo.clientInfo,
                write
            ];
            sequences.PATCH = [
                cookies.getCookies,
                clientInfo.getInfo,
                debugInfo.addForm,
                parseRequest.getPost,
                parseRequest.getGet,
                debugInfo.addInfo,
                write
            ];
            sequences.UNDEFINED = [
                cookies.getCookies,
                clientInfo.getInfo,
                debugInfo.addForm,
                parseRequest.getPost,
                parseRequest.getGet,
                debugInfo.requestInfo,
                debugInfo.clientInfo,
                debugInfo.addConfig,
                write
            ];

            if (server.config.global.forceHTTPS == true) {
                http.server(forceHTTPS);
            } else {
                http.server(respond);
            }

            https.server(respond);

            // Process shutdown for
            process.on('message', function (msg) {
                if (msg === 'shutdown') {
                    // initiate graceful close of any connections to server
                }
            });
        }
    }
}